
try{
    var path = require("path");	
    var initAdvLog = require('advlog');
    // Init AdvLog-Node.js
    var logfilepath = path.resolve("./config/log.json");
    initAdvLog(logfilepath);
    }catch(e){
    
    };


var MAX_TEST = 10000;
var time = 0;
var test = function()
{
    time = getMSTime();
    for(var i=0; i < MAX_TEST; i++ ){
        console.error('Mssage num= ' + i );
        console.warn('Mssage num= ' + i );
        console.log('Mssage num= ' + i );
        console.debug('Mssage num= ' + i );
        console.trace('Mssage num= ' + i );                         
    }
    var duration =  getMSTime() - time;
    console.info('Performance count = ' + MAX_TEST + ' Time = ' +  duration + ' ms');
}


// enable remove function
setInterval(function () {
    test();
    }, 1000);




